mapper.insert:
INSERT INTO rechg_stt_list (
                              RECHG_STT_LIST_ID
                            , PROVIDER_ID
                            , ST_ID
                            , CHGR_ID
                            , MW_KIND_CD
                            , R_TIME_YN
                            , CH_ID
                            , PLUG_TYPE_CD
                            , MEM_AUTH_INPUT_NO
                            , RECHG_SDT
                            , RECHG_DEMAND_AMT
                            , RECHG_EST_TIME
                            , CHGR_PAYING_YN
                            , PAY_TYPE_CD
                            , MEM_AUTH_ID
                            , CREDIT_P_PAY_TRX_NO

                            , CREDIT_P_PAY_TRX_DT

                            , INTEGRATED_WH
                            , CURR_VOLT
                            , CURR_C
                            , DATA_CREATE_DT
                            , CHGR_TX_DT
                            , REG_DT
                  ) VALUES (
                              (SELECT CONCAT(DATE_FORMAT(NOW(), '%Y%m%d'), LPAD(IFNULL(max(right(rsl.RECHG_STT_LIST_ID,5)), 0)+1, 8, '0')) AS RECHG_STT_LIST_ID
                                 FROM rechg_stt_list rsl
                                WHERE LEFT(rsl.RECHG_STT_LIST_ID, 8) = DATE_FORMAT(NOW(), '%Y%m%d')
                              )
                            , #{providerId}
                            , #{stId}
                            , #{chgrId}
                            , #{mwKindCd}
                            , #{rTimeYn}
                            , #{chId}
                            , #{plugTypeCd}
                            , #{memAuthInputNo}
                            , #{rechgSdt}
                            , #{rechgDemandAmt}
                            , #{rechgEstTime}
                            , (SELECT chgr.PAYING_YN AS PAYING_YN
                                 FROM chgr chgr
                                WHERE 1=1
                                  AND chgr.PROVIDER_ID = #{providerId}
                                  AND chgr.ST_ID       = #{stId}
                                  AND chgr.CHGR_ID     = #{chgrId}
                              )
                            , #{payTypeCd}
                            , (
                                SELECT mal.MEM_AUTH_ID MEM_AUTH_ID
                                  FROM mem_auth_list mal
                                 WHERE 1=1
                                   AND mal.PROVIDER_ID = #{providerId}
                                   AND mal.ST_ID       = #{stId}
                                   AND mal.CHGR_ID     = #{chgrId}
                                   AND mal.CH_ID       = #{chId}
                              ORDER BY mal.REG_DT
                                  DESC LIMIT 1
                              )
                            , #{creditPPayTrxNo}

                            , #{creditPPayTrxDt}

                            , #{integratedWh}
                            , #{currVolt}
                            , #{currC}
                            , #{dataCreateDt}
                            , #{chgrTxDt}
                            , now()
                           )

INSERT INTO rechging_list (
                            RECHGING_LIST_ID
                            , PROVIDER_ID
                            , ST_ID
                            , CHGR_ID
                            , MW_KIND_CD
                            , R_TIME_YN
                            , CH_ID
                            , PLUG_TYPE_CD
                            , MEM_AUTH_INPUT_NO
                            , RECHG_STT_LIST_ID
                            , RECHG_SDT
                            , RECHG_DEMAND_AMT
                            , RECHGING_AMT
                            , RECHGING_WH
                            , RECHG_REMAIN_TIME
                            , CHGR_PAYING_YN
                            , PAY_TYPE_CD
                            , MEM_AUTH_ID
                            , CREDIT_P_PAY_TRX_NO

                            , CREDIT_P_PAY_TRX_DT

                            , INTEGRATED_WH
                            , CURR_VOLT
                            , CURR_C
                            , DATA_CREATE_DT
                            , CHGR_TX_DT
                            , REG_DT
                ) VALUES (
                              (SELECT CONCAT(DATE_FORMAT(NOW(), '%Y%m%d'), LPAD(IFNULL(max(right(rl.RECHGING_LIST_ID,5)), 0)+1, 8, '0')) AS RECHGING_LIST_ID
                                 FROM rechging_list rl
                                WHERE LEFT(rl.RECHGING_LIST_ID, 8) = DATE_FORMAT(NOW(), '%Y%m%d')
                              )
                            , #{providerId}
                            , #{stId}
                            , #{chgrId}
                            , #{mwKindCd}
                            , #{rTimeYn}
                            , #{chId}
                            , #{plugTypeCd}
                            , #{memAuthInputNo}
                            , (
                                SELECT rsl.RECHG_STT_LIST_ID RECHG_STT_LIST_ID
                                  FROM rechg_stt_list rsl
                                 WHERE 1=1
                                   AND rsl.PROVIDER_ID = #{providerId}
                                   AND rsl.ST_ID       = #{stId}
                                   AND rsl.CHGR_ID     = #{chgrId}
                                   AND rsl.CH_ID       = #{chId}
                                   AND rsl.R_TIME_YN   = 'Y'
                              ORDER BY rsl.REG_DT
                                  DESC LIMIT 1
                              )
                            , #{rechgSdt}
                            , #{rechgDemandAmt}
                            , #{rechgingAmt}
                            , #{rechgingWh}
                            , #{rechgRemainTime}
                            , (SELECT chgr.PAYING_YN AS PAYING_YN
                                 FROM chgr chgr
                                WHERE 1=1
                                  AND chgr.PROVIDER_ID = #{providerId}
                                  AND chgr.ST_ID       = #{stId}
                                  AND chgr.CHGR_ID     = #{chgrId}
                              )
                            , #{payTypeCd}
                            , (
                                SELECT mal.MEM_AUTH_ID MEM_AUTH_ID
                                  FROM mem_auth_list mal
                                 WHERE 1=1
                                   AND mal.PROVIDER_ID = #{providerId}
                                   AND mal.ST_ID       = #{stId}
                                   AND mal.CHGR_ID     = #{chgrId}
                                   AND mal.CH_ID       = #{chId}
                              ORDER BY mal.REG_DT
                                  DESC LIMIT 1
                              )
                            , #{creditPPayTrxNo}

                            , #{creditPPayTrxDt}

                            , #{integratedWh}
                            , #{currVolt}
                            , #{currC}
                            , #{dataCreateDt}
                            , #{chgrTxDt}
                            , now()
                )

