                                     FROM chgr chgr
                                    WHERE 1=1
                                      AND chgr.PROVIDER_ID = #{providerId}
                                      AND chgr.ST_ID       = #{stId}
                                      AND chgr.CHGR_ID     = #{chgrId}
                              )
                                , #{payTypeCd}
                                , (
                                    SELECT mal.MEM_AUTH_ID MEM_AUTH_ID
