mapper.update:
UPDATE chgr

   SET SPEED_TP_CD      = #{speedTpCd}
     , GPS_XPOS         = #{gpsXpos}
     , GPS_YPOS         = #{gpsYpos}
     , MF_CD            = #{mfCd}
     , M2M_MF_CD        = #{m2mMfCd}
     , RF_MF_CD         = #{rfMfCd}
     , M2M_TEL          = #{m2mTel}
     , VAN_IP           = #{vanIp}
     , VAN_PORT         = #{vanPort}
     , OFFLINE_FREE_WH  = #{offlineFreeWh}
     , MW_SESSION       = #{mwSession}
     , MOD_UID          = #{modUid}
     , MOD_DT           = now()

 WHERE 1=1

   AND PROVIDER_ID      = #{providerId}
   AND ST_ID            = #{stId}
   AND CHGR_ID          = #{chgrId}
