mapper.update:
UPDATE ctrl_list
   SET MW_KIND_CD   =   #{mwKindCd}
     , RES_DT       =   #{resDt}
     , RES_CD       =   #{resCd}
     , RES_RSN_CD   =   #{resRsnCd}

 WHERE 1=1

   AND CTRL_LIST_ID    =   #{ctrlListId}

mapper.insert:
INSERT INTO TX_MSG_LIST (
                            CTRL_LIST_ID
                            , UUID
                            , DATA_CREATE_DT
                            , CHGR_TX_DT
                            , TX_MSG

              ) VALUES (

                            #{ctrlListId}
                            , #{uuid}
                            , #{dataCreateDt}
                            , #{chgrTxDt}
                            , #{txMsg}

                        )

mapper.update:
UPDATE tx_msg_list

   SET RES_DT       =   #{resDt}
     , RES_CD       =   #{resCd}
     , RES_RSN_CD   =   #{resRsnCd}
     , RES_MSG      =   #{resMsg}

 WHERE 1=1

   AND UUID         =   #{uuid}
