INSERT INTO mem_auth_list (
                            MEM_AUTH_ID
                          , PROVIDER_ID
                          , ST_ID
                          , CHGR_ID
                          , CH_ID
                          , MW_KIND_CD
                          , MEM_AUTH_REQ_DT
                          , MEM_AUTH_INPUT_NO
                          , RES_DT
                          , AUTH_RSLT_CD
                          , AUTH_RSLT_VALID
                          , MEM_PROVIDER_ID
                          , PRICE
                          , CHGR_TX_DT
                          , REG_DT
                ) VALUES (
                            (SELECT CONCAT(DATE_FORMAT(NOW(), '%Y%m%d'), LPAD(IFNULL(max(right(mal.MEM_AUTH_ID,5)), 0)+1, 8, '0')) AS MEM_AUTH_ID
                                FROM mem_auth_list mal
                                WHERE LEFT(mal.MEM_AUTH_ID, 8) = DATE_FORMAT(NOW(), '%Y%m%d')
                            )
                          , #{providerId}
                          , #{stId}
                          , #{chgrId}
                          , #{chId}
                          , #{mwKindCd}
                          , #{memAuthReqDt}
                          , #{memAuthInputNo}
                          , #{resDt}
                          , #{authRsltCd}
                          , #{authRsltValid}
                          , #{memProviderId}
                          , #{price}
                          , #{chgrTxDt}
                          , now()
                          )
